export function shouldFetchFpt(state, reference) {
    const posts = state.fptReducer.fptByReference[reference]

    if (!posts) {
        return true
    } else if (posts.isFetching) {
        return false
    } else {
        //bxslider fix
        delete(state.fptReducer.fptByReference[reference]);
        //was loaded
        return false
    }
}

export function fetchFptIfNeeded(reference) {
    return (dispatch, getState) => {
        if (shouldFetchFpt(getState(), reference)) {
            return dispatch(fetchFpt(getState(), reference))
        }
    }
}